import { useVocabularyImage } from '../hooks/useVocabularyImage';

interface VocabularyImageProps {
  word: string;
  className?: string;
}

export function VocabularyImage({ word, className = '' }: VocabularyImageProps) {
  const imageUrl = useVocabularyImage(word);

  return (
    <img
      src={imageUrl}
      alt={word}
      className={className}
      onError={(e) => {
        e.currentTarget.src = 'data:image/svg+xml;base64,';
      }}
    />
  );
}
